local VarenneBfAQuests = VarenneBfAQuests
local VarenneBfAQuestsFrame = CreateFrame("Frame")

VarenneBfAQuestsFrame:RegisterEvent("QUEST_COMPLETE")
VarenneBfAQuestsFrame:RegisterEvent("ADDON_LOADED")

function VarenneBfAQuests:prepareData(data)
    local result = {}
    for k, v in pairs(data) do
        local resultVal = ""

        for questId, QuestName in pairs(v) do
            resultVal = QuestName

            if (self.QuestsCompleted[questId] == true) then
                resultVal = "DONE " .. resultVal
            elseif (self.QuestsCompleted[questId] == false) then
                resultVal = "IN QUEST LOG " .. resultVal
            end
        end

        result[k] = k .. ": " .. resultVal
    end

    return result
end


VarenneBfAQuestsFrame:SetScript("OnEvent", function(self, event, ...)
    if event == "ADDON_LOADED" and addonName == self.ADDON_NAME then
        VarenneBfAQuests.QuestsCompleted = GetQuestsCompleted()
        SLASH_VARENNEBFAQQUESTS1 = '/vdtq';
        SlashCmdList["VARENNEBFAQQUESTS"] = function(args)
            --print("hi")
            for k, v in pairs(VarenneBfAQuests.Quests) do
                ViragDevTool_AddData(VarenneBfAQuests:prepareData(v), k)
            end
        end
    end
end)